// Returns an update function that operates on the given data
function updateAuthorCounts(boundData) {
  return function (d, i) {
    // Gotta catch 'em all!
    if(!d) return;
    if(!d.Name) return;

    // filter by type
    var types = d3.selectAll('#filterType label'),
        activeType = null;
    types.each(function() {
      var thisEl = d3.select(this),
          active = thisEl.attr('data-active') == 'true';
      if(active) activeType = thisEl.text().trim();
    });

    if (activeType && activeType != 'All' && d.Type != activeType) return;

    if(paperCount[d['Conf.']] == undefined) paperCount[d['Conf.']] = 0;
    paperCount[d['Conf.']] = paperCount[d['Conf.']] + 1;

    // Add paper authors to the author count tracker
    d.Name.forEach(function(a, ai) {
      var paper = {'Conf.': boundData[i]['Conf.'], i:data.indexOf(d), Topic:boundData[i].Topic};
      if(authorCount[a] == undefined) authorCount[a] = [];
      authorCount[a].push(paper);

      if(ai == 0) {
        if(firstAuthorCount[a] == undefined) firstAuthorCount[a] = [];
        firstAuthorCount[a].push(paper);
      }

      if(ai == d.Name.length - 1) {
        if(lastAuthorCount[a] == undefined) lastAuthorCount[a] = [];
        lastAuthorCount[a].push(paper);
      }
    });
  }
}

var updateAuthorCountsFn = updateAuthorCounts(data);
data.forEach(function(d,i) {
  d.Name = d.Name.replace(' and ',', ').replace(', and ',', ').split(', ');
  d.Type == d.Type.trim();
  updateAuthorCountsFn(d,i);
});

var paperData = data.filter(function(d) {
      return d.Type == 'Papers';
    });

function unzip(data) {
  return Object.keys(data).map(function(d) {
    var papers = data[d];
    papers.sort(function(a,b) {
      return a['Conf.'] < b['Conf.'] ? -1 : 1;
    });
    return {author: d, papers:data[d]};
  });
}

authorCount = unzip(authorCount);
firstAuthorCount = unzip(firstAuthorCount);
lastAuthorCount = unzip(lastAuthorCount);

