// Define filtering behavior
function filterData() {
  var titleInput = d3.select('#titleSearch').node().value.toLowerCase(),
      authorInput = d3.select('#authorSearch').node().value.toLowerCase();

  var newData = data;

  // filter by type
  var types = d3.selectAll('#filterType label'),
      activeType = null;
  types.each(function() {
    var thisEl = d3.select(this),
        active = thisEl.attr('data-active') == 'true';
    if(active) activeType = thisEl.text().trim();
  });

  if(activeType != null && activeType != 'All') {
    newData = newData.filter(function(d) {
      var type = d['Type'].trim();
      return type == activeType;
    });
  }

  // Filter by award
  if (d3.select('#filterByAward').node().checked) {
    newData = newData.filter(function(d) {
      return d.Award != '';
    });
  }

  // Filter by conference
  var conferences = d3.selectAll('#conferenceFilterGroup label'),
      activeConferences = [];
  conferences.each(function() {
    var thisEl = d3.select(this),
        active = thisEl.attr('data-active') == 'true';
    if(active) activeConferences.push(thisEl.text());
  });

  // If any conferences have been activated
  if(activeConferences.length > 0) {
    newData = newData.filter(function(d) {
      var conference = d['Conf.'];
      var show = activeConferences.reduce(function(prev, current, i, array) {
        if(prev) return true;
        else if(conference.indexOf(current) > -1) return true;
        else return false;
      }, false);
      return show;
    });
  }

  // Filter by day
  var days = d3.selectAll('#filterDay label'),
      activeDays = [];
  days.each(function() {
    var thisEl = d3.select(this),
        active = thisEl.attr('data-active') == 'true';
    if(active) activeDays.push(thisEl.select('input').attr('data-day'));//console.log(thisEl.select('input').attr('data-day'));
  });

  // If any days have been activated
  if(activeDays.length > 0) {
    newData = newData.filter(function(d) {
      var day = d.Day;
      var show = activeDays.reduce(function(prev, current, i, array) {
        if(prev) return true;
        else if(day.indexOf(current) > -1) return true;
        else return false;
      }, false);
      return show;
    });
  }

  // Filter by time
  var startTime = d3.select('#startTime').node().value,
      endTime = d3.select('#endTime').node().value;
  if(startTime != '' && startTime.split(':').length == 2) {
    var startTimeH = parseInt(startTime.split(':')[0]),
        startTimeM = parseInt(startTime.split(':')[1]),
        startTime = new Date(),
        tmpDate = new Date(startTime.getTime());

    startTime.setHours(startTimeH);
    startTime.setMinutes(startTimeM);
    startTime.setMilliseconds(0);

    newData = newData.filter(function(d) {
      var tmpTime = d.Time || '',
          tmpStart = d.Time.split('–')[0].trim(),
          tmpH = parseInt(tmpStart.split(':')[0]),
          tmpM = parseInt(tmpStart.split(':')[0]) || null;

      tmpDate.setHours(tmpH);
      tmpDate.setMinutes(tmpM);
      tmpDate.setMilliseconds(0);

      return tmpDate.getTime() >= startTime.getTime();
    });
  }
  if(endTime != '' && endTime.split(':').length == 2) {
    var endTimeH = parseInt(endTime.split(':')[0]),
        endTimeM = parseInt(endTime.split(':')[1]),
        endTime = new Date(),
        tmpDate = new Date();

    endTime.setHours(endTimeH);
    endTime.setMinutes(endTimeM);
    endTime.setMilliseconds(0);

    newData = newData.filter(function(d) {
      var tmpTime = d.Time || '',
          tmpEnd = d.Time.split('–')[1].trim();
      console.log(tmpEnd);
      var tmpH = parseInt(tmpEnd.split(':')[0]),
          tmpM = parseInt(tmpEnd.split(':')[1]) || null;

      tmpDate.setHours(tmpH);
      tmpDate.setMinutes(tmpM);
      tmpDate.setMilliseconds(0);

      return endTime.getTime() >= tmpDate.getTime();
    });
  }

  // Filter by title
  newData = newData.filter(function(d) {
    return d.Topic.toLowerCase().indexOf(titleInput) > -1;
  });

  // Filter by author
  newData = newData.filter(function(d) {
    var result = d.Name.reduce(function(last, current, i, array) {
      if (last) return true;
      else if (array[i].toLowerCase().indexOf(authorInput) > -1) return true;
      else return false;
    }, false);
    return result;
  });

  paperListingAreaBody.selectAll('tr').remove();
  paperListingAreaBody.selectAll('tr')
      .data(newData)
      .enter()
      .append('tr')
      .each(addPaperRow);

  // Update visualization data
  authorCount = {};
  firstAuthorCount = {};
  lastAuthorCount = {};

  var updateAuthorCountsFn = updateAuthorCounts(newData);
  newData.forEach(updateAuthorCountsFn);

  authorCount = unzip(authorCount);
  firstAuthorCount = unzip(firstAuthorCount);
  lastAuthorCount = unzip(lastAuthorCount);

  chartsDiv.selectAll('div').remove();
  if(authorCount.length == 0) return;
  input.title = 'All Authors';
  allAuthorsChart = chartsDiv.append('div').style(chartDivStyle)
        .datum(authorCount)
        .call(barChart(input).addFullData(data));

  input.title = 'First Authors';
  firstAuthorsChart = chartsDiv.append('div').style(chartDivStyle)
        .datum(firstAuthorCount)
        .call(barChart(input).addFullData(data));

  input.title = 'Last Authors'
  lastAuthorsChart = chartsDiv.append('div').style(chartDivStyle)
        .datum(lastAuthorCount)
        .call(barChart(input).addFullData(data));
}

